mapper.select:
SELECT 
	
FROM sec_activity_apply a

WHERE a.id = #{id}

SELECT
FROM sec_activity_apply a
1=1
AND sa.title like '%'||#{title}||'%' '%'+#{title}+'%' CONCAT('%', #{title}, '%')
AND u3.nickname like '%'||#{user.nickname}||'%' '%'+#{user.nickname}+'%' CONCAT('%', #{user.nickname}, '%')
AND a.sign_type = #{signType}
AND a.activity_id = #{activityId}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC
