mapper.select:
SELECT 
	
FROM sec_activity a

WHERE a.id = #{id}

SELECT 
	
FROM sec_activity a


	1=1 
	
		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')
	
	
		AND a.type = #{type} 
	
	
		AND a.activity_status = #{activityStatus} 
	
	
		AND a.state = #{state} 
	
	
		AND a.begin_date >= #{beginDate} 
	
	
		AND a.begin_date <= #{endDate} 
	


	
		ORDER BY ${page.orderBy}
	
	
		ORDER BY a.update_date DESC
