mapper.select:
SELECT 
	
FROM sec_order a

WHERE a.order_id = #{orderId}

SELECT 
	
FROM sec_order a


	1=1 
	
		AND a.orderId like 
				'%'||#{orderId}||'%'
				'%'+#{orderId}+'%'
				CONCAT('%', #{orderId}, '%')
	
	
		AND a.order_type = #{orderType}
	
	
		AND a.pay_type = #{payType}
	
	
		AND a.pay_status = #{payStatus}
	


	
		ORDER BY ${page.orderBy}
	
	
		ORDER BY a.update_date DESC
