mapper.select:
SELECT 
	
FROM sec_user a

WHERE a.id = #{id}

SELECT 
	
FROM sec_user a

 1=1 
	
		AND a.nickname like 
				'%'||#{nickname}||'%'
				'%'+#{nickname}+'%'
				CONCAT('%', #{nickname}, '%')
	
	
		AND a.user_type = #{userType}
	
	
		AND a.status = #{status}
	
	
		AND a.is_real_name = #{isRealName}
	


	
		ORDER BY ${page.orderBy}
	
	
		ORDER BY a.update_date DESC
